INSERT INTO `official_words` VALUES (1,'كلمة رئيس مجلس الادارة','Chairman speech','<p style=\"text-align:justify\">يعكس إنشاء الهيئة العامة للمنشآت الصغيرة والمتوسطة ودعم إيراداتها حرص واهتمام القيادة الرشيدة بأهمية قطاع المنشآت الصغيرة والمتوسطة ايمانا بدورها في زيادة مساهمة الناتج المحلي من ٢٠٪&rlm; إلى ٣٥٪&rlm; وفق رؤية المملكة ٢٠٣٠ إن من أولويات الهيئة العامة للمنشآت الصغيرة والمتوسطة، معالجة ثلاثة تحديات تتمثل في الكوادر البشرية، البيروقراطية الحكومية، وفرص التمويل&quot;، ووضع آليات لمعالجتها عن طريق التشاركية مع الجهات ذات العلاقة، إذ تعتبر المنشآت الصغيرة والمتوسطة ركيزة أساسية في التنمية الاقتصادية في أي مكان في العالم وستسعى الهيئة بشكل دائم إلى تقديم الدعم الفني والاستشارات وتطوير المنشآت الصغيرة والمتوسطة وزيادة قدرتها التنافسية لتحقيق قيمة مضافة للاقتصاد الوطني، خصوصا أن المنشآت الصغيرة والمتوسطة تلعب اليوم دورا كبيرا في تعزيز الاقتصاد والتنويع الاقتصادي، وتساهم مساهمة فاعلة في تحقيق التنمية المستدامة وتوفير فرص العمل وإيجاد بيئة داعمة وحاضنة للإبداع والابتكار</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><strong>معالي الدكتور ماجد بن عبدالله القصبي</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#419dab\">رئيس مجلس إدارة الهيئة العامة للمنشآت الصغيرة والمتوسطة</span></p>','<p style=\"text-align:justify\">يعكس إنشاء الهيئة العامة للمنشآت الصغيرة والمتوسطة ودعم إيراداتها حرص واهتمام القيادة الرشيدة بأهمية قطاع المنشآت الصغيرة والمتوسطة ايمانا بدورها في زيادة مساهمة الناتج المحلي من ٢٠٪&rlm; إلى ٣٥٪&rlm; وفق رؤية المملكة ٢٠٣٠ إن من أولويات الهيئة العامة للمنشآت الصغيرة والمتوسطة، معالجة ثلاثة تحديات تتمثل في الكوادر البشرية، البيروقراطية الحكومية، وفرص التمويل&quot;، ووضع آليات لمعالجتها عن طريق التشاركية مع الجهات ذات العلاقة، إذ تعتبر المنشآت الصغيرة والمتوسطة ركيزة أساسية في التنمية الاقتصادية في أي مكان في العالم وستسعى الهيئة بشكل دائم إلى تقديم الدعم الفني والاستشارات وتطوير المنشآت الصغيرة والمتوسطة وزيادة قدرتها التنافسية لتحقيق قيمة مضافة للاقتصاد الوطني، خصوصا أن المنشآت الصغيرة والمتوسطة تلعب اليوم دورا كبيرا في تعزيز الاقتصاد والتنويع الاقتصادي، وتساهم مساهمة فاعلة في تحقيق التنمية المستدامة وتوفير فرص العمل وإيجاد بيئة داعمة وحاضنة للإبداع والابتكار</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><strong>معالي الدكتور ماجد بن عبدالله القصبي</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#419dab\">رئيس مجلس إدارة الهيئة العامة للمنشآت الصغيرة والمتوسطة</span></p>',NULL,'2017-09-14 14:22:00','2018-01-30 13:21:31','chairman-97790.png','-','-','<p style=\"text-align:justify\">&nbsp; &nbsp; &nbsp;</p>','<div id=\"gt-res-content\">\r\n<div class=\"trans-verified-button-small\" dir=\"ltr\" id=\"gt-res-dir-ctr\">\r\n<div id=\"tts_button\">&nbsp; &nbsp; &nbsp; &nbsp;</div>\r\n</div>\r\n</div>','governor-18151.png',0);
